package assignment;

public class Fibbonacci {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int  firstNum = 0;
		int secNum = 1;
		int sum = 0;
		System.out.println(firstNum);
	for(int i=0; i<11; i++);
	sum =firstNum+secNum;
	secNum=firstNum;
	sum=secNum;
	System.out.println(sum);

}
}
